import React from 'react'
import { useState } from "react";
import Axios from "axios";
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';


export default function Play() {

  const [words, setWords] = useState([{img:"car", word:"Car"},{img:"banana", word:"Banana"},{img:"bottle", word:"Bottle"},{img:"dog", word:"Dog"}]);

  const newGame = () => {
    Axios.get("http://localhost:3001/play").then((response) => {
      setWords(response.data);
      console.log(response.data);
    });
  };

  return (
    <div>
    <button onClick={newGame}>Play</button>
    <ImageList sx={{ width: 500, height: 450 }} cols={2} rowHeight={164}>
        <ImageListItem>
          <img
            src={require(`../assets/img/${words[1].img}.JPG?w=164&h=164&fit=crop&auto=format`)}
            alt={words[1].word}
            loading="lazy"
          />
        </ImageListItem>
        <ImageListItem>
          <img
            src={require(`../assets/img/${words[2].img}.JPG?w=164&h=164&fit=crop&auto=format`)}
            alt={words[2].word}
            loading="lazy"
          />
        </ImageListItem>
        <ImageListItem>
          <img
            src={require(`../assets/img/${words[3].img}.JPG?w=164&h=164&fit=crop&auto=format`)}
            alt={words[3].word}
            loading="lazy"
          />
        </ImageListItem>
        <ImageListItem>
          <img
            src={(`../assets/img/${words[4].img}.JPG?w=164&h=164&fit=crop&auto=format`)}
            alt={words[4].word}
            loading="lazy"
          />
        </ImageListItem>
    </ImageList>
    </div>
  );
}
